package br.unibave.bpr.exercicios2_1_1;import java.io.File;import java.io.IOException;import java.io.PrintWriter;public class PrintToFile implements Print {    private File file;    public PrintToFile(final String fileName) {        this.file = new File(fileName);    }    public void print(final String content) {        try {            file.createNewFile();            PrintWriter writer = new PrintWriter(file);            writer.print(content);            writer.close();            System.out.println("Impress√£o em arquivo, verifique: " + file.getAbsolutePath());        } catch (IOException e) {            System.out.println(String.format("Erro ao tentar criar o arquivo %s (%s)",                    file.getAbsolutePath(), e.getMessage()));        }    }}