package br.unibave.bpr.exercicios2_3_1;import java.util.Arrays;public class Main {	public static void main(String[] args) {		AbstractName namesDefault = new NamesDefault();		AbstractName namesReverse = new NamesReverse();		Arrays.stream(args)				.forEach(n->{					if (n.contains(",")) {						namesReverse.addName(n);					} else {						namesDefault.addName(n);					}				});		namesDefault.printNames();		namesReverse.printNames();	}}