package br.unibave.bpr.exercicios2_3_2;import java.io.File;import java.io.FileNotFoundException;import java.util.Scanner;public abstract class AbstractProvider {    protected abstract String retrieveInfo() throws Exception;    public void showInfo() throws Exception {        System.out.println(retrieveInfo());    }    protected Scanner newScan(File arquivo) throws Exception {        try {            return new Scanner(arquivo);        } catch (FileNotFoundException e) {            System.out.println(String.format("Arquivo n√£o encontrado: %s", arquivo.getPath()));            System.exit(1);        }        return null;    }}