package br.unibave.bpr.exercicios2_3_2;import java.io.File;public class Public extends AbstractProvider {	private final String PATH = "/br/unibave/bpr/exercicios2_3_2/resources/publico.txt";	protected String retrieveInfo() throws Exception {		File archive = new File(Public.class.getProtectionDomain()				.getCodeSource().getLocation().getPath()+PATH);		ResourceUtils<Public> resourceUtils = new ResourceUtils<>();		return resourceUtils.getContentFile(archive, this);	}}