package br.unibave.bpr.exercicios2_3_3;import java.util.Objects;public abstract class LogPrinter {    private AbstractLogTool log;    protected abstract AbstractLogTool newLogTool();    public void escreverLog(String mensagem) {        if (Objects.isNull(log)) {            log = newLogTool();        }        log.logRegister(mensagem);    }}